          start,
          end;

      var bracketRegexp = {
        '(': /[()]/, ')': /[()]/,
        '[': /[[\]]/, ']': /[[\]]/,
        '{': /[{}]/, '}': /[{}]/ }[symb];
      var openSym = {
        '(': '(', ')': '(',
        '[': '[', ']': '[',
        '{': '{', '}': '{' }[symb];
